const gulp = require('gulp'),
    rename = require('gulp-rename2'),
    clean = require('gulp-clean'),
    uglify = require('gulp-uglify'),
    ts = require('gulp-typescript');

const wwwroot = './wwwroot/',
    sourceroot = './Scripts/';

const paths = {
